import api from './api';

/**
 * Service for project related API calls
 */
const projectService = {
    /**
     * Get all projects
     * @returns {Promise<Array>} List of projects
     */
    getProjects: async () => {
        return await api.get('/projects');
    },

    /**
     * Get a specific project by ID
     * @param {string} projectId - Project ID
     * @returns {Promise<Object>} Project data
     */
    getProjectById: async (projectId) => {
        return await api.get(`/projects/${projectId}`);
    },

    // Alias for backward compatibility
    getProject: async (projectId) => {
        return await projectService.getProjectById(projectId);
    },

    /**
     * Create a new project
     * @param {Object} projectData - Project data
     * @returns {Promise<Object>} Created project
     */
    createProject: async (projectData) => {
        return await api.post('/projects', projectData);
    },

    /**
     * Update a project
     * @param {string} projectId - Project ID
     * @param {Object} projectData - Updated project data
     * @returns {Promise<Object>} Updated project
     */
    updateProject: async (projectId, projectData) => {
        return await api.put(`/projects/${projectId}`, projectData);
    },

    /**
     * Delete a project
     * @param {string} projectId - Project ID
     * @returns {Promise<Object>} Success message
     */
    deleteProject: async (projectId) => {
        return await api.delete(`/projects/${projectId}`);
    },

    /**
     * Add a member to a project by email
     * @param {string} projectId - Project ID
     * @param {Object} data - Object containing email of user to add
     * @returns {Promise<Object>} Updated project
     */
    addMember: async (projectId, data) => {
        return await api.post(`/projects/${projectId}/members`, data);
    },

    /**
     * Remove a member from a project
     * @param {string} projectId - Project ID
     * @param {string} userId - User ID to remove
     * @returns {Promise<Object>} Updated project
     */
    removeMember: async (projectId, userId) => {
        return await api.delete(`/projects/${projectId}/members/${userId}`);
    },

    // Aliases for backward compatibility
    addProjectMember: async (projectId, userId) => {
        return await projectService.addMember(projectId, { userId });
    },

    removeProjectMember: async (projectId, userId) => {
        return await projectService.removeMember(projectId, userId);
    }
};

export default projectService;
