import React, { useState, useEffect } from 'react';
import { Link as RouterLink, Navigate } from 'react-router-dom';
import {
    Container,
    Box,
    Typography,
    TextField,
    Button,
    Link,
    Paper,
    InputAdornment,
    IconButton,
    CircularProgress,
    Alert
} from '@mui/material';
import {
    Visibility,
    VisibilityOff,
    LockOutlined as LockIcon
} from '@mui/icons-material';

import { useAuth } from '../context/AuthContext';
import api from '../services/api';

const Login = () => {
    const { login, isAuthenticated, loading } = useAuth();
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);
    const [errors, setErrors] = useState({});
    const [formError, setFormError] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [serverStatus, setServerStatus] = useState('checking');

    // Check if backend is running
    useEffect(() => {
        const checkServer = async () => {
            try {
                await api.get('/');
                setServerStatus('online');
            } catch (error) {
                console.error('Server check failed:', error);
                setServerStatus('offline');
            }
        };

        checkServer();
    }, []);

    // If already authenticated, redirect to dashboard
    if (isAuthenticated) {
        return <Navigate to="/dashboard" replace />;
    }

    // Handle input change
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        // Clear error when user types
        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: ''
            }));
        }

        // Clear form error when user types
        if (formError) {
            setFormError('');
        }
    };

    // Toggle password visibility
    const handleTogglePassword = () => {
        setShowPassword(prev => !prev);
    };

    // Validate form
    const validateForm = () => {
        const newErrors = {};

        if (!formData.email) {
            newErrors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Email is invalid';
        }

        if (!formData.password) {
            newErrors.password = 'Password is required';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    // Handle direct login (bypassing auth context for debugging)
    const handleDirectLogin = async () => {
        if (!validateForm()) return;

        try {
            setSubmitting(true);
            setFormError('');

            console.log('Attempting direct login with:', formData.email);

            // Make direct API call
            const response = await fetch('http://localhost:5000/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: formData.email,
                    password: formData.password
                })
            });

            const data = await response.json();
            console.log('Direct login response:', data);

            if (data.success && data.data && data.data.user) {
                localStorage.setItem('token', data.data.user.token);
                window.location.href = '/dashboard';
                return;
            }

            setFormError(data.message || 'Login failed. Please try again.');

        } catch (error) {
            console.error('Direct login error:', error);
            setFormError('Network error. Please check if the server is running.');
        } finally {
            setSubmitting(false);
        }
    };

    // Handle form submission through auth context
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) return;

        try {
            setSubmitting(true);
            setFormError('');

            console.log('Submitting login form with:', { email: formData.email });
            const success = await login(formData.email, formData.password);

            if (!success) {
                setFormError('Login failed. Please check your credentials and try again.');
            }
        } catch (error) {
            console.error('Login submission error:', error);
            setFormError(error.message || 'An unexpected error occurred. Please try again.');
        } finally {
            setSubmitting(false);
        }
    };

    if (serverStatus === 'offline') {
        return (
            <Box className="auth-container">
                <Container maxWidth="sm">
                    <Paper elevation={3} className="auth-form">
                        <Box sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                            p: 4
                        }}>
                            <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
                                Backend server is not running. Please start the backend server before attempting to log in.
                            </Alert>
                            <Typography variant="body1" gutterBottom>
                                To start the backend server:
                            </Typography>
                            <Typography variant="body2" component="pre" sx={{
                                backgroundColor: 'grey.100',
                                p: 2,
                                borderRadius: 1,
                                width: '100%',
                                overflow: 'auto'
                            }}>
                                cd backend<br />
                                npm start
                            </Typography>
                        </Box>
                    </Paper>
                </Container>
            </Box>
        );
    }

    return (
        <Box className="auth-container">
            <Container maxWidth="sm">
                <Paper elevation={3} className="auth-form">
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                        }}
                    >
                        <Box sx={{
                            bgcolor: 'primary.main',
                            color: 'white',
                            borderRadius: '50%',
                            p: 1,
                            mb: 2
                        }}>
                            <LockIcon fontSize="large" />
                        </Box>
                        <Typography component="h1" variant="h5" gutterBottom>
                            Sign in
                        </Typography>
                        <Typography variant="body2" color="text.secondary" align="center" sx={{ mb: 3 }}>
                            Welcome back! Please enter your credentials to access your account.
                        </Typography>

                        {formError && (
                            <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
                                {formError}
                            </Alert>
                        )}

                        <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1, width: '100%' }}>
                            <TextField
                                margin="normal"
                                required
                                fullWidth
                                id="email"
                                label="Email Address"
                                name="email"
                                autoComplete="email"
                                autoFocus
                                value={formData.email}
                                onChange={handleChange}
                                error={!!errors.email}
                                helperText={errors.email}
                                disabled={submitting || loading}
                            />
                            <TextField
                                margin="normal"
                                required
                                fullWidth
                                name="password"
                                label="Password"
                                type={showPassword ? 'text' : 'password'}
                                id="password"
                                autoComplete="current-password"
                                value={formData.password}
                                onChange={handleChange}
                                error={!!errors.password}
                                helperText={errors.password}
                                disabled={submitting || loading}
                                InputProps={{
                                    endAdornment: (
                                        <InputAdornment position="end">
                                            <IconButton
                                                aria-label="toggle password visibility"
                                                onClick={handleTogglePassword}
                                                edge="end"
                                            >
                                                {showPassword ? <VisibilityOff /> : <Visibility />}
                                            </IconButton>
                                        </InputAdornment>
                                    )
                                }}
                            />

                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                sx={{ mt: 3, mb: 2 }}
                                disabled={submitting || loading}
                            >
                                {(submitting || loading) ? <CircularProgress size={24} /> : 'Sign In'}
                            </Button>

                            <Button
                                fullWidth
                                variant="outlined"
                                sx={{ mb: 2 }}
                                disabled={submitting || loading}
                                onClick={handleDirectLogin}
                            >
                                Direct Login (Debug)
                            </Button>

                            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                                <Link component={RouterLink} to="/forgot-password" variant="body2">
                                    Forgot password?
                                </Link>
                                <Link component={RouterLink} to="/register" variant="body2">
                                    {"Don't have an account? Sign Up"}
                                </Link>
                            </Box>
                        </Box>
                    </Box>
                </Paper>
            </Container>
        </Box>
    );
};

export default Login;
