import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    Box,
    Typography,
    Button,
    Paper,
    Grid,
    TextField,
    MenuItem,
    FormControl,
    InputLabel,
    Select,
    CircularProgress,
    Breadcrumbs,
    Link as MuiLink
} from '@mui/material';
import { DatePicker } from '@mui/lab';
import { Link } from 'react-router-dom';
import { Add as AddIcon, ArrowBack as ArrowBackIcon } from '@mui/icons-material';

import { useTask } from '../context/TaskContext';
import { useProject } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';

const TaskCreate = () => {
    const navigate = useNavigate();
    const { createTask, loading: taskLoading } = useTask();
    const { projects, fetchProjects, loading: projectsLoading } = useProject();
    const { user } = useAuth();

    const [taskData, setTaskData] = useState({
        title: '',
        description: '',
        project: '',
        status: 'To-Do',
        priority: 'Medium',
        dueDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // Default 1 week from now
        assignee: user?._id || '' // Default to current user
    });

    const [errors, setErrors] = useState({});

    // Fetch projects on component mount
    useEffect(() => {
        fetchProjects();
    }, [fetchProjects]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setTaskData({
            ...taskData,
            [name]: value
        });

        // Clear error for this field if it exists
        if (errors[name]) {
            setErrors({
                ...errors,
                [name]: null
            });
        }
    };

    const handleDateChange = (date) => {
        setTaskData({
            ...taskData,
            dueDate: date
        });

        // Clear dueDate error if it exists
        if (errors.dueDate) {
            setErrors({
                ...errors,
                dueDate: null
            });
        }
    };

    const validateForm = () => {
        const newErrors = {};

        if (!taskData.title.trim()) {
            newErrors.title = 'Title is required';
        }

        if (!taskData.description.trim()) {
            newErrors.description = 'Description is required';
        }

        if (!taskData.project) {
            newErrors.project = 'Project is required';
        }

        if (!taskData.dueDate) {
            newErrors.dueDate = 'Due date is required';
        } else if (new Date(taskData.dueDate) < new Date()) {
            newErrors.dueDate = 'Due date must be in the future';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return;
        }

        const result = await createTask({
            ...taskData,
            dueDate: taskData.dueDate ? new Date(taskData.dueDate).toISOString() : null
        });

        if (result) {
            navigate(`/tasks/${result._id}`);
        }
    };

    return (
        <Box sx={{ py: 4, px: 2 }}>
            <Box mb={3}>
                <Breadcrumbs aria-label="breadcrumb">
                    <MuiLink component={Link} to="/dashboard" underline="hover" color="inherit">
                        Dashboard
                    </MuiLink>
                    <MuiLink component={Link} to="/tasks" underline="hover" color="inherit">
                        Tasks
                    </MuiLink>
                    <Typography color="text.primary">New Task</Typography>
                </Breadcrumbs>
            </Box>

            <Box display="flex" alignItems="center" mb={4}>
                <Button
                    component={Link}
                    to="/tasks"
                    startIcon={<ArrowBackIcon />}
                    sx={{ mr: 2 }}
                >
                    Back to Tasks
                </Button>
                <Typography variant="h4" component="h1">
                    Create New Task
                </Typography>
            </Box>

            <Paper sx={{ p: 3 }}>
                <form onSubmit={handleSubmit}>
                    <Grid container spacing={3}>
                        <Grid item xs={12}>
                            <TextField
                                name="title"
                                label="Task Title"
                                value={taskData.title}
                                onChange={handleChange}
                                fullWidth
                                required
                                error={!!errors.title}
                                helperText={errors.title}
                            />
                        </Grid>

                        <Grid item xs={12}>
                            <TextField
                                name="description"
                                label="Task Description"
                                value={taskData.description}
                                onChange={handleChange}
                                fullWidth
                                multiline
                                rows={4}
                                required
                                error={!!errors.description}
                                helperText={errors.description}
                            />
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <FormControl fullWidth error={!!errors.project}>
                                <InputLabel>Project *</InputLabel>
                                <Select
                                    name="project"
                                    value={taskData.project}
                                    onChange={handleChange}
                                    required
                                    label="Project *"
                                >
                                    <MenuItem value="" disabled>
                                        <em>Select a project</em>
                                    </MenuItem>
                                    {projectsLoading ? (
                                        <MenuItem disabled>
                                            <CircularProgress size={20} sx={{ mr: 1 }} /> Loading...
                                        </MenuItem>
                                    ) : (
                                        projects && projects.map((project) => (
                                            <MenuItem key={project._id} value={project._id}>
                                                {project.name}
                                            </MenuItem>
                                        ))
                                    )}
                                </Select>
                                {errors.project && (
                                    <Typography variant="caption" color="error">
                                        {errors.project}
                                    </Typography>
                                )}
                            </FormControl>
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <TextField
                                name="dueDate"
                                label="Due Date"
                                type="date"
                                value={taskData.dueDate ? new Date(taskData.dueDate).toISOString().split('T')[0] : ''}
                                onChange={handleChange}
                                fullWidth
                                required
                                error={!!errors.dueDate}
                                helperText={errors.dueDate}
                                InputLabelProps={{
                                    shrink: true,
                                }}
                            />
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <FormControl fullWidth>
                                <InputLabel>Status</InputLabel>
                                <Select
                                    name="status"
                                    value={taskData.status}
                                    onChange={handleChange}
                                    label="Status"
                                >
                                    <MenuItem value="To-Do">To-Do</MenuItem>
                                    <MenuItem value="In Progress">In Progress</MenuItem>
                                    <MenuItem value="Completed">Completed</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <FormControl fullWidth>
                                <InputLabel>Priority</InputLabel>
                                <Select
                                    name="priority"
                                    value={taskData.priority}
                                    onChange={handleChange}
                                    label="Priority"
                                >
                                    <MenuItem value="Low">Low</MenuItem>
                                    <MenuItem value="Medium">Medium</MenuItem>
                                    <MenuItem value="High">High</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>

                        <Grid item xs={12}>
                            <Box display="flex" justifyContent="flex-end" mt={2}>
                                <Button
                                    type="button"
                                    onClick={() => navigate('/tasks')}
                                    sx={{ mr: 2 }}
                                >
                                    Cancel
                                </Button>
                                <Button
                                    type="submit"
                                    variant="contained"
                                    color="primary"
                                    startIcon={<AddIcon />}
                                    disabled={taskLoading}
                                >
                                    {taskLoading ? <CircularProgress size={24} /> : 'Create Task'}
                                </Button>
                            </Box>
                        </Grid>
                    </Grid>
                </form>
            </Paper>
        </Box>
    );
};

export default TaskCreate;
